const fs = require(''fs'');
const parameterGroupsCode = fs.readFileSync(''src/components/UnifiedContextMenu/utils/parameterGroups.js'', ''utf8'');
const displayStoreCode = fs.readFileSync(''src/stores/displayStore.js'', ''utf8'');

// Extract displayStore defaultConfig parameters
const defaultConfigMatch = displayStoreCode.match(/defaultConfig: \{([\s\S]*?)^\s\}/m);
if (defaultConfigMatch) {
  const configParams = defaultConfigMatch[1].match(/^\s*[a-zA-Z_][a-zA-Z0-9_]*:/gm);
  const configParamNames = configParams ? configParams.map(p => p.replace(/^\s*:/, '''')) : [];

  // Extract schema parameters
  const schemaMatch = parameterGroupsCode.match(/parameters: \[([\s\S]*?)\]/g);
  const allGroupParams = [];
  if (schemaMatch) {
    schemaMatch.forEach(group => {
      const params = group.match(/''([a-zA-Z_][a-zA-Z0-9_]*)''/g);
      if (params) {
        allGroupParams.push(...params.map(p => p.replace(/''/g, '''')));
      }
    });
  }

  const configSet = new Set(configParamNames);
  const groupSet = new Set(allGroupParams);

  const missingInGroups = configParamNames.filter(p => !groupSet.has(p));
  const extraInGroups = allGroupParams.filter(p => !configSet.has(p));

  console.log(''=== DETAILED PARAMETER ANALYSIS ==='');
  console.log(''Config parameters:'', configParamNames.length);
  console.log(''Group parameters:'', allGroupParams.length);
  console.log('''');
  console.log(''Missing in groups (exist in config but not in parameter groups):'');
  missingInGroups.forEach(p => console.log(''  -'', p));
  console.log('''');
  console.log(''Extra in groups (exist in parameter groups but not in config):'');
  extraInGroups.forEach(p => console.log(''  -'', p));
  console.log('''');
  console.log(''Parameters that exist in both:'');
  const common = configParamNames.filter(p => groupSet.has(p));
  console.log(''Count:'', common.length);
  common.forEach(p => console.log(''  -'', p));
}
")
